import React, { createContext, useState, useEffect, useContext } from 'react';

interface User {
    id: number;
    email: string;
    username: string;
    is_beginner: boolean | null;
}

interface AuthContextType {
    user: User | null;
    isLoading: boolean;
    setUser: (user: User | null) => void;
    checkAuth: () => Promise<void>;
}

const AuthContext = createContext<AuthContextType>({
    user: null,
    isLoading: true,
    setUser: () => { },
    checkAuth: async () => { }
});

export const useAuth = () => useContext(AuthContext);

export const AuthProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
    const [user, setUser] = useState<User | null>(null);
    const [isLoading, setIsLoading] = useState(true);

    const checkAuth = async () => {
        try {
            const response = await fetch('/api/auth/me');
            if (response.ok) {
                const data = await response.json();
                setUser(data.user);
            } else {
                setUser(null);
            }
        } catch (error) {
            console.error("Auth check failed", error);
            setUser(null);
        } finally {
            setIsLoading(false);
        }
    };

    useEffect(() => {
        checkAuth();
    }, []);

    return (
        <AuthContext.Provider value={{ user, isLoading, setUser, checkAuth }}>
            {children}
        </AuthContext.Provider>
    );
};

import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { apiGet, apiPost, showToast } from '../utils/helpers';

interface Architecture {
    key: string;
    name: string;
    short: string;
    desc: string;
    icon: string;
}

const Architect: React.FC = () => {
    const navigate = useNavigate();

    // State
    const [architectures, setArchitectures] = useState<Architecture[]>([]);
    const [selectedArch, setSelectedArch] = useState<string | null>(null);
    const [layers, setLayers] = useState<number[]>([128, 64]);

    // Hyperparameters
    const [epochs, setEpochs] = useState<number>(50);
    const [lr, setLr] = useState<number>(0.001);
    const [batchSize, setBatchSize] = useState<number>(32);
    const [optimizer, setOptimizer] = useState<string>('adam');
    const [activation, setActivation] = useState<string>('relu');
    const [projectName, setProjectName] = useState<string>('VortexProject');

    // Early Stopping
    const [esEnabled, setEsEnabled] = useState<boolean>(false);
    const [esPatience, setEsPatience] = useState<number>(10);
    const [esDelta, setEsDelta] = useState<number>(0.0001);

    // Upload state
    const [isDragging, setIsDragging] = useState(false);
    const [isUploading, setIsUploading] = useState(false);
    const [weightsLoaded, setWeightsLoaded] = useState(false);
    const [weightStatus, setWeightStatus] = useState<string>('');
    const weightInputRef = useRef<HTMLInputElement>(null);

    const canvasRef = useRef<HTMLCanvasElement>(null);

    // Initial load
    useEffect(() => {
        let mounted = true;

        const init = async () => {
            try {
                const archs = await apiGet('/api/architectures');
                if (mounted) setArchitectures(archs);

                const state = await apiGet('/api/state');
                if (mounted && state.has_model && state.model_config) {
                    const cfg = state.model_config;
                    setSelectedArch(cfg.arch_type);
                    setLayers(cfg.layer_sizes || [128, 64]);
                    setEpochs(cfg.epochs || 50);
                    setLr(cfg.lr || 0.001);
                    setBatchSize(cfg.batch_size || 32);
                    setOptimizer(cfg.optimizer || 'adam');
                    setActivation(cfg.activation || 'relu');
                    setProjectName(cfg.project_name || 'VortexProject');

                    if (cfg.early_stopping && cfg.early_stopping.enabled) {
                        setEsEnabled(true);
                        setEsPatience(cfg.early_stopping.patience || 10);
                        setEsDelta(cfg.early_stopping.min_delta || 0.0001);
                    }
                }
            } catch (e) {
                console.error(e);
            }
        };
        init();

        return () => { mounted = false; };
    }, []);

    // Canvas drawing
    useEffect(() => {
        if (!selectedArch || !canvasRef.current) return;

        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');
        if (!ctx) return;

        const dpr = window.devicePixelRatio || 1;
        const rect = canvas.getBoundingClientRect();

        // Ensure size matches CSS exactly
        if (rect.width === 0 || rect.height === 0) return;

        canvas.width = rect.width * dpr;
        canvas.height = rect.height * dpr;
        ctx.setTransform(dpr, 0, 0, dpr, 0, 0); // Reset transform instead of scale

        const W = rect.width;
        const H = rect.height;

        ctx.clearRect(0, 0, W, H);

        const allLayers = [4, ...layers, 2];
        const numLayers = allLayers.length;
        const layerSpacing = W / (numLayers + 1);
        const maxDisplay = 8;

        const positions: { x: number, y: number }[][] = [];

        for (let l = 0; l < numLayers; l++) {
            const x = layerSpacing * (l + 1);
            const n = Math.min(allLayers[l], maxDisplay);
            const neuronSpacing = Math.min(40, (H - 60) / (n + 1));
            const startY = H / 2 - (neuronSpacing * (n - 1)) / 2;
            const layerPositions = [];

            for (let i = 0; i < n; i++) {
                layerPositions.push({ x, y: startY + i * neuronSpacing });
            }
            positions.push(layerPositions);
        }

        // Draw connections
        for (let l = 0; l < positions.length - 1; l++) {
            for (const from of positions[l]) {
                for (const to of positions[l + 1]) {
                    ctx.beginPath();
                    ctx.moveTo(from.x, from.y);
                    ctx.lineTo(to.x, to.y);
                    ctx.strokeStyle = 'rgba(99, 102, 241, 0.12)';
                    ctx.lineWidth = 1;
                    ctx.stroke();
                }
            }
        }

        // Draw neurons
        for (let l = 0; l < positions.length; l++) {
            const isInput = l === 0;
            const isOutput = l === positions.length - 1;

            for (const pos of positions[l]) {
                ctx.beginPath();
                ctx.arc(pos.x, pos.y, 10, 0, Math.PI * 2);

                if (isInput) {
                    ctx.fillStyle = 'rgba(6, 182, 212, 0.6)';
                    ctx.strokeStyle = 'rgba(6, 182, 212, 0.8)';
                } else if (isOutput) {
                    ctx.fillStyle = 'rgba(249, 115, 22, 0.6)';
                    ctx.strokeStyle = 'rgba(249, 115, 22, 0.8)';
                } else {
                    ctx.fillStyle = 'rgba(139, 92, 246, 0.5)';
                    ctx.strokeStyle = 'rgba(139, 92, 246, 0.8)';
                }

                ctx.lineWidth = 2;
                ctx.fill();
                ctx.stroke();
            }

            // Label
            if (positions[l].length > 0) {
                const x = positions[l][0].x;
                const bottomY = positions[l][positions[l].length - 1].y + 28;
                ctx.fillStyle = 'rgba(157, 157, 186, 0.8)';
                ctx.font = '11px "Space Grotesk", sans-serif';
                ctx.textAlign = 'center';

                if (isInput) {
                    ctx.fillText('Input', x, bottomY);
                } else if (isOutput) {
                    ctx.fillText('Output', x, bottomY);
                } else {
                    ctx.fillText(`${allLayers[l]}n`, x, bottomY);
                }

                // Truncation indicator
                if (allLayers[l] > maxDisplay) {
                    ctx.fillStyle = 'rgba(139, 92, 246, 0.6)';
                    ctx.fillText('‚ãÆ', x, H / 2 + 4);
                }
            }
        }
    }, [layers, selectedArch]);

    // Resize listener for canvas
    useEffect(() => {
        const handleResize = () => {
            // Force re-render of canvas by updating a dummy state or just triggering drawing if possible
            // Using layers reference in dependency array covers most redraws, but window resize needs a trigger
            setLayers(l => [...l]);
        };
        window.addEventListener('resize', handleResize);
        return () => window.removeEventListener('resize', handleResize);
    }, []);

    const uploadWeightFile = async (file: File) => {
        setIsUploading(true);
        try {
            const formData = new FormData();
            formData.append('file', file);

            const res = await fetch('/api/weights/upload', {
                method: 'POST',
                body: formData,
            });
            const data = await res.json();

            if (data.error) {
                showToast(data.error, 'error');
                setIsUploading(false);
                return;
            }

            const cfg = data.config;
            setWeightsLoaded(true);
            setSelectedArch(cfg.arch_type);
            setLayers(cfg.layer_sizes || [128, 64]);
            setEpochs(cfg.epochs || 50);
            setLr(cfg.lr || 0.001);
            setBatchSize(cfg.batch_size || 32);
            setOptimizer(cfg.optimizer || 'adam');
            setActivation(cfg.activation || 'relu');
            setProjectName(cfg.project_name || 'VortexProject');

            setWeightStatus(`Loaded: ${file.name} ‚Äî ${cfg.arch_type.toUpperCase()}, ${(cfg.layer_sizes || []).join('‚Üí')} neurons`);
            showToast(`Weights loaded! Architecture auto-configured as ${cfg.arch_type.toUpperCase()}`, 'success');
        } catch (e: any) {
            showToast('Error uploading weights: ' + e.message, 'error');
        }
        setIsUploading(false);
    };

    const handleAddLayer = () => {
        const last = layers[layers.length - 1] || 64;
        setLayers([...layers, Math.max(16, Math.floor(last / 2))]);
    };

    const handleRemoveLayer = (index: number) => {
        if (layers.length <= 1) return;
        setLayers(layers.filter((_, i) => i !== index));
    };

    const updateLayer = (index: number, val: number) => {
        const newLayers = [...layers];
        newLayers[index] = Math.max(1, Math.min(2048, val || 64));
        setLayers(newLayers);
    };

    const handleStartTraining = async () => {
        if (!selectedArch) {
            showToast('Please select a network architecture.', 'error');
            return;
        }

        const config = {
            arch_type: selectedArch,
            layer_sizes: layers,
            epochs,
            lr,
            batch_size: batchSize,
            optimizer,
            activation,
            project_name: projectName,
            early_stopping: {
                enabled: esEnabled,
                patience: esPatience,
                min_delta: esDelta,
            }
        };

        try {
            const btn = document.getElementById('btn-start-training') as HTMLButtonElement;
            if (btn) btn.disabled = true;

            const res = await apiPost('/api/model/configure', config);
            if (res.error) {
                showToast(res.error, 'error');
                if (btn) btn.disabled = false;
                return;
            }

            showToast('Model configured! Redirecting to training...', 'success');
            setTimeout(() => navigate('/training'), 800);
        } catch (e: any) {
            showToast('Error: ' + e.message, 'error');
            const btn = document.getElementById('btn-start-training') as HTMLButtonElement;
            if (btn) btn.disabled = false;
        }
    };

    return (
        <>
            <div className="page-header">
                <h1>üß† Architecture Builder</h1>
                <p>Choose a neural network type, configure layers, and set hyperparameters.</p>
            </div>

            {/* Weight Upload Zone */}
            <div className="glass-panel">
                <div className="panel-title"><span className="pt-icon">üì¶</span> Load Pretrained Weights</div>
                <p className="text-muted mb-2">Upload a <code>.pt</code> weight file to auto-restore the architecture and hyperparameters.</p>

                {!weightsLoaded && (
                    <div
                        className={`weight-upload-zone ${isDragging ? 'drag-over' : ''} ${isUploading ? 'uploading' : ''}`}
                        onClick={() => weightInputRef.current?.click()}
                        onDragOver={e => { e.preventDefault(); setIsDragging(true); }}
                        onDragLeave={() => setIsDragging(false)}
                        onDrop={e => {
                            e.preventDefault();
                            setIsDragging(false);
                            const file = e.dataTransfer.files[0];
                            if (file && file.name.endsWith('.pt')) uploadWeightFile(file);
                            else showToast('Only .pt weight files are supported', 'error');
                        }}
                    >
                        <div className="upload-zone-content">
                            <span className="upload-zone-icon">‚¨ÜÔ∏è</span>
                            <span className="upload-zone-text">Drag & drop a <strong>.pt</strong> file here, or <span className="upload-zone-link">browse</span></span>
                        </div>
                        <input
                            type="file"
                            ref={weightInputRef}
                            accept=".pt"
                            className="hidden-input"
                            style={{ display: 'none' }}
                            onChange={e => e.target.files && uploadWeightFile(e.target.files[0])}
                        />
                    </div>
                )}

                {weightsLoaded && (
                    <div className="weight-upload-status">
                        <span className="upload-status-icon">‚úÖ</span>
                        <span className="upload-status-text">{weightStatus}</span>
                    </div>
                )}
            </div>

            {/* Architecture Selection */}
            <div className="glass-panel">
                <div className="panel-title"><span className="pt-icon">üî¨</span> Select Architecture</div>
                <div className="arch-grid">
                    {architectures.length === 0 ? (
                        <div className="text-center text-muted" style={{ gridColumn: '1/-1' }}>
                            <div className="spinner"></div>
                            <p className="mt-1">Loading architectures...</p>
                        </div>
                    ) : (
                        architectures.map(a => (
                            <div
                                key={a.key}
                                className={`arch-card ${selectedArch === a.key ? 'selected' : ''}`}
                                onClick={() => setSelectedArch(a.key)}
                            >
                                <div className="arch-icon">{a.icon}</div>
                                <div className="arch-name">{a.name}</div>
                                <div className="arch-short">{a.short}</div>
                                <div className="arch-desc">{a.desc}</div>
                            </div>
                        ))
                    )}
                </div>
            </div>

            {selectedArch && (
                <>
                    {/* Layer Configurator */}
                    <div className="glass-panel">
                        <div className="panel-title"><span className="pt-icon">üìê</span> Configure Layers</div>
                        <p className="text-muted mb-2">Add hidden layers and set the number of neurons in each.</p>

                        <div className="layer-list">
                            {layers.map((l, i) => (
                                <div key={i} className="layer-item">
                                    <span className="layer-label">Layer {i + 1}</span>
                                    <input
                                        type="number"
                                        value={l}
                                        min="1" max="2048"
                                        className="layer-neurons"
                                        onChange={e => updateLayer(i, parseInt(e.target.value) || 0)}
                                    />
                                    <span className="text-muted" style={{ fontSize: '0.8rem' }}>neurons</span>
                                    <button
                                        className="remove-layer"
                                        onClick={() => handleRemoveLayer(i)}
                                        disabled={layers.length <= 1}
                                        style={{ opacity: layers.length <= 1 ? 0.3 : 1 }}
                                    >√ó</button>
                                </div>
                            ))}
                        </div>
                        <div className="mt-2">
                            <button className="btn btn-secondary btn-sm" onClick={handleAddLayer}>+ Add Layer</button>
                        </div>
                    </div>

                    {/* Hyperparameters */}
                    <div className="glass-panel">
                        <div className="panel-title"><span className="pt-icon">‚öôÔ∏è</span> Hyperparameters</div>
                        <div className="form-row">
                            <div className="form-group">
                                <label className="form-label">Epochs</label>
                                <input type="number" className="form-input" value={epochs} onChange={e => setEpochs(parseInt(e.target.value) || 50)} />
                            </div>
                            <div className="form-group">
                                <label className="form-label">Learning Rate</label>
                                <input type="number" className="form-input" step="0.0001" value={lr} onChange={e => setLr(parseFloat(e.target.value) || 0.001)} />
                            </div>
                            <div className="form-group">
                                <label className="form-label">Batch Size</label>
                                <select className="form-select" value={batchSize} onChange={e => setBatchSize(parseInt(e.target.value) || 32)}>
                                    <option value="16">16</option>
                                    <option value="32">32</option>
                                    <option value="64">64</option>
                                    <option value="128">128</option>
                                    <option value="256">256</option>
                                </select>
                            </div>
                            <div className="form-group">
                                <label className="form-label">Optimizer</label>
                                <select className="form-select" value={optimizer} onChange={e => setOptimizer(e.target.value)}>
                                    <option value="adam">Adam</option>
                                    <option value="adamw">AdamW</option>
                                    <option value="sgd">SGD</option>
                                    <option value="rmsprop">RMSprop</option>
                                </select>
                            </div>
                            <div className="form-group">
                                <label className="form-label">Activation</label>
                                <select className="form-select" value={activation} onChange={e => setActivation(e.target.value)}>
                                    <option value="relu">ReLU</option>
                                    <option value="leaky_relu">Leaky ReLU</option>
                                    <option value="elu">ELU</option>
                                    <option value="selu">SELU</option>
                                    <option value="gelu">GELU</option>
                                    <option value="tanh">Tanh</option>
                                    <option value="sigmoid">Sigmoid</option>
                                </select>
                            </div>
                        </div>

                        {/* Early Stopping */}
                        <div className="es-section">
                            <div className="es-header">
                                <label className="toggle-switch">
                                    <input type="checkbox" checked={esEnabled} onChange={e => setEsEnabled(e.target.checked)} />
                                    <span className="toggle-slider"></span>
                                </label>
                                <span className="es-label">Early Stopping</span>
                            </div>
                            {esEnabled && (
                                <div className="es-params">
                                    <div className="form-row">
                                        <div className="form-group">
                                            <label className="form-label">Patience</label>
                                            <input type="number" className="form-input" value={esPatience} onChange={e => setEsPatience(parseInt(e.target.value) || 10)} />
                                            <span className="form-hint">Epochs to wait for improvement</span>
                                        </div>
                                        <div className="form-group">
                                            <label className="form-label">Min Delta</label>
                                            <input type="number" className="form-input" step="0.0001" value={esDelta} onChange={e => setEsDelta(parseFloat(e.target.value) || 0.0001)} />
                                            <span className="form-hint">Minimum change to qualify as improvement</span>
                                        </div>
                                    </div>
                                </div>
                            )}
                        </div>

                        {/* Project Name */}
                        <div className="form-row mt-2">
                            <div className="form-group" style={{ flex: 1 }}>
                                <label className="form-label">Project Name</label>
                                <input type="text" className="form-input" value={projectName} onChange={e => setProjectName(e.target.value)} placeholder="MyProject" />
                                <span className="form-hint">Used in the weight filename</span>
                            </div>
                        </div>
                    </div>

                    {/* Canvas Preview */}
                    <div className="glass-panel">
                        <div className="panel-title"><span className="pt-icon">üëÅÔ∏è</span> Architecture Preview</div>
                        <canvas ref={canvasRef} className="network-viz" style={{ width: '100%', height: '300px' }}></canvas>
                    </div>

                    <div className="action-row">
                        <button className="btn btn-secondary" onClick={() => navigate('/dataset')}>‚Üê Back to Dataset</button>
                        <button className="btn btn-primary" id="btn-start-training" onClick={handleStartTraining}>Start Training ‚ö°</button>
                    </div>
                </>
            )}
        </>
    );
};

export default Architect;

import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { apiGet, showToast } from '../utils/helpers';
import TextCourse from '../components/courses/TextCourse';
import VideoCourse from '../components/courses/VideoCourse';
import LabCourse from '../components/courses/LabCourse';

const CourseTypeIcon: Record<string, string> = {
    text: 'üìÑ',
    video: 'üé•',
    lab: 'üî¨'
};

const CourseDetail: React.FC = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [course, setCourse] = useState<any>(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        apiGet(`/api/courses/${id}`)
            .then(data => {
                if (data.error) showToast(data.error, 'error');
                else setCourse(data);
            })
            .catch(err => showToast(`Failed to load course: ${err.message}`, 'error'))
            .finally(() => setLoading(false));
    }, [id]);

    if (loading) {
        return <div className="page-header"><h1>Loading course module...</h1></div>;
    }

    if (!course) {
        return (
            <div className="page-header text-center">
                <h1>‚ö†Ô∏è Course Not Found</h1>
                <button className="btn btn-primary mt-2" onClick={() => navigate('/courses')}>‚Üê Back to Courses</button>
            </div>
        );
    }

    return (
        <div className="course-detail-container">
            <div className="flex align-center gap-1 mb-2">
                <button className="btn btn-sm btn-ghost" onClick={() => navigate('/courses')}>
                    ‚Üê Back
                </button>
            </div>

            <div className="page-header mb-2" style={{ textAlign: 'left', padding: 0 }}>
                <div className="flex gap-1 align-center mb-05">
                    <span className="course-detail-icon">{CourseTypeIcon[course.type]}</span>
                    <span className="course-detail-type uppercase gradient-text font-mono" style={{ fontSize: '0.8rem' }}>
                        {course.type} MODULE ‚Ä¢ {course.duration}
                    </span>
                </div>
                <h1 style={{ marginTop: '0.5rem' }}>{course.title}</h1>
                <p className="text-muted" style={{ maxWidth: '800px', textAlign: 'left', margin: 0 }}>{course.description}</p>
                <div className="flex gap-05 mt-1">
                    {course.tags?.map((tag: string) => (
                        <span key={tag} className="bento-tag bento-tag-sm">{tag}</span>
                    ))}
                </div>
            </div>

            <div className="course-content-area mt-2">
                {course.type === 'text' && <TextCourse content={course.content || ''} />}
                {course.type === 'video' && <VideoCourse videoUrl={course.videoUrl || course.video_url || ''} transcript={course.transcript} />}
                {course.type === 'lab' && <LabCourse notebookUrl={course.notebookUrl || course.notebook_url || ''} />}
            </div>
        </div>
    );
};

export default CourseDetail;

import React, { useState, useEffect, useRef } from 'react';
import type { DragEvent, ChangeEvent } from 'react';
import { useNavigate } from 'react-router-dom';
import { apiGet, apiPost, showToast } from '../utils/helpers';

interface ColumnInfo {
    name: string;
    dtype: string;
    is_numeric: boolean;
    unique: number;
}

interface DatasetInfo {
    rows: number;
    cols: number;
    columns: ColumnInfo[];
    preview: Record<string, any>[];
}

const Dataset: React.FC = () => {
    const navigate = useNavigate();
    const [step, setStep] = useState<number>(1);

    const [isDragging, setIsDragging] = useState(false);
    const [uploading, setUploading] = useState(false);
    const [uploadProgress, setUploadProgress] = useState(0);
    const [uploadStatusText, setUploadStatusText] = useState('Uploading...');

    const [datasetInfo, setDatasetInfo] = useState<DatasetInfo | null>(null);
    const [filename, setFilename] = useState('');

    const [selectedFeatures, setSelectedFeatures] = useState<Set<string>>(new Set());
    const [selectedTarget, setSelectedTarget] = useState<string | null>(null);
    const fileInputRef = useRef<HTMLInputElement>(null);

    // Initial state check
    useEffect(() => {
        let mounted = true;
        apiGet('/api/state')
            .then(state => {
                if (state.has_dataset && mounted) {
                    apiGet('/api/dataset/analyze').then(data => {
                        if (data.info && mounted) {
                            setDatasetInfo(data.info);
                            setFilename(data.filename);
                            setStep(3);

                            if (state.has_features) {
                                setSelectedFeatures(new Set(state.feature_cols));
                                if (state.target_col) {
                                    setSelectedTarget(state.target_col);
                                }
                            }
                        }
                    });
                }
            })
            .catch(() => { });

        return () => { mounted = false; };
    }, []);

    const handleDragOver = (e: DragEvent<HTMLDivElement>) => {
        e.preventDefault();
        setIsDragging(true);
    };

    const handleDragLeave = (e: DragEvent<HTMLDivElement>) => {
        e.preventDefault();
        setIsDragging(false);
    };

    const handleDrop = (e: DragEvent<HTMLDivElement>) => {
        e.preventDefault();
        setIsDragging(false);
        const file = e.dataTransfer.files[0];
        if (file) uploadFile(file);
    };

    const handleFileInput = (e: ChangeEvent<HTMLInputElement>) => {
        if (e.target.files && e.target.files[0]) {
            uploadFile(e.target.files[0]);
        }
    };

    const openFileDialog = () => {
        if (fileInputRef.current) fileInputRef.current.click();
    };

    const uploadFile = async (file: File) => {
        const valid = /\.(csv|xlsx|xls)$/i.test(file.name);
        if (!valid) {
            showToast('Only CSV and Excel files are supported.', 'error');
            return;
        }

        setUploading(true);
        setUploadProgress(30);
        setUploadStatusText(`Uploading ${file.name}...`);

        const formData = new FormData();
        formData.append('file', file);

        try {
            setUploadProgress(60);
            const res = await fetch('/api/upload', { method: 'POST', body: formData });
            const data = await res.json();

            if (data.error) {
                showToast(data.error, 'error');
                setUploading(false);
                return;
            }

            setUploadProgress(100);
            setUploadStatusText('Upload complete!');

            setTimeout(() => {
                setFilename(data.filename);
                setDatasetInfo(data.info);
                setStep(3);
                setSelectedFeatures(new Set());
                setSelectedTarget(null);
                setUploading(false);
            }, 400);

        } catch (err: any) {
            showToast('Upload failed: ' + err.message, 'error');
            setUploading(false);
        }
    };

    const toggleFeature = (colName: string) => {
        const newFeatures = new Set(selectedFeatures);
        if (newFeatures.has(colName)) {
            newFeatures.delete(colName);
        } else {
            if (selectedTarget === colName) setSelectedTarget(null);
            newFeatures.add(colName);
        }
        setSelectedFeatures(newFeatures);
    };

    const setTarget = (colName: string) => {
        const newFeatures = new Set(selectedFeatures);
        if (newFeatures.has(colName)) {
            newFeatures.delete(colName);
            setSelectedFeatures(newFeatures);
        }
        setSelectedTarget(colName);
    };

    const handleContinue = async (e: React.MouseEvent) => {
        e.preventDefault();
        if (selectedFeatures.size === 0 || !selectedTarget) {
            showToast('Select at least one feature and one target column.', 'error');
            return;
        }

        try {
            const res = await apiPost('/api/dataset/configure', {
                feature_cols: Array.from(selectedFeatures),
                target_col: selectedTarget,
            });

            if (res.error) {
                showToast(res.error, 'error');
                return;
            }

            showToast('Dataset configured! Redirecting...', 'success');
            navigate('/architect');
        } catch (err: any) {
            showToast('Error: ' + err.message, 'error');
        }
    };

    const isReady = selectedFeatures.size > 0 && selectedTarget !== null;

    return (
        <>
            <div className="page-header">
                <h1>üìä Dataset Designer</h1>
                <p>Upload your data, preview it, and select the columns for training.</p>
            </div>

            <div className="steps">
                {[1, 2, 3].map((num) => (
                    <div key={num} className={`step ${step === num ? 'active' : ''} ${step > num ? 'completed' : ''}`}>
                        <div className="step-number">{num}</div>
                        <span className="step-text">
                            {num === 1 ? 'Upload File' : num === 2 ? 'Preview Data' : 'Select Columns'}
                        </span>
                    </div>
                ))}
            </div>

            {/* Step 1: Upload */}
            <div className={`glass-panel ${step >= 2 ? 'hidden' : ''}`} id="upload-section">
                <div className="panel-title"><span className="pt-icon">üìÅ</span> Upload Dataset</div>
                <div
                    className={`upload-zone ${isDragging ? 'dragover' : ''}`}
                    onDragOver={handleDragOver}
                    onDragLeave={handleDragLeave}
                    onDrop={handleDrop}
                    onClick={openFileDialog}
                >
                    <div className="upload-icon">‚¨ÜÔ∏è</div>
                    <h3>Drag & drop your file here</h3>
                    <p>or click to browse ‚Äî CSV, XLSX, XLS supported (max 100 MB)</p>
                    <input
                        type="file"
                        ref={fileInputRef}
                        style={{ display: 'none' }}
                        accept=".csv,.xlsx,.xls"
                        onChange={handleFileInput}
                    />
                </div>

                {uploading && (
                    <div className="upload-progress active">
                        <div className="progress-bar-container">
                            <div className="progress-bar" style={{ width: `${uploadProgress}%` }}></div>
                        </div>
                        <p className="text-muted mt-1 text-center">{uploadStatusText}</p>
                    </div>
                )}
            </div>

            {/* Step 2 & 3 Combined View (once dataset is loaded) */}
            {datasetInfo && (
                <>
                    <div className="glass-panel">
                        <div className="flex-between mb-2">
                            <div className="panel-title mb-0"><span className="pt-icon">üëÅÔ∏è</span> Data Preview</div>
                            <div>
                                <span className="badge badge-success">‚úì {filename}</span>
                            </div>
                        </div>

                        <div className="summary-row">
                            <div className="summary-item"><div className="si-label">Rows</div><div className="si-value">{datasetInfo.rows.toLocaleString()}</div></div>
                            <div className="summary-item"><div className="si-label">Columns</div><div className="si-value">{datasetInfo.cols}</div></div>
                            <div className="summary-item"><div className="si-label">Numeric</div><div className="si-value">{datasetInfo.columns.filter(c => c.is_numeric).length}</div></div>
                            <div className="summary-item"><div className="si-label">Categorical</div><div className="si-value">{datasetInfo.columns.filter(c => !c.is_numeric).length}</div></div>
                        </div>

                        <div className="data-table-wrapper">
                            <table className="data-table">
                                <thead>
                                    <tr>
                                        {datasetInfo.columns.map(c => <th key={c.name}>{c.name}</th>)}
                                    </tr>
                                </thead>
                                <tbody>
                                    {datasetInfo.preview.map((row, i) => (
                                        <tr key={i}>
                                            {datasetInfo.columns.map(c => <td key={c.name}>{row[c.name] ?? ''}</td>)}
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        </div>
                    </div>

                    <div className="glass-panel">
                        <div className="panel-title"><span className="pt-icon">üéØ</span> Select Columns</div>
                        <p className="text-muted mb-2">
                            Click <strong>Feature</strong> to mark input variables, and <strong>Target</strong> to select what the model
                            should predict.
                        </p>

                        <div className="column-picker">
                            {datasetInfo.columns.map(col => {
                                const isFeature = selectedFeatures.has(col.name);
                                const isTarget = selectedTarget === col.name;

                                return (
                                    <div key={col.name} className={`col-item ${isFeature ? 'selected-feature' : ''} ${isTarget ? 'selected-target' : ''}`}>
                                        <div>
                                            <div className="col-name">{col.name}</div>
                                            <div className="col-dtype">{col.dtype} {col.is_numeric ? `¬∑ ${col.unique} unique` : `¬∑ ${col.unique} categories`}</div>
                                        </div>
                                        <div className="col-controls">
                                            <button
                                                className={`col-btn btn-feat ${isFeature ? 'active-feature' : ''}`}
                                                onClick={() => toggleFeature(col.name)}
                                            >
                                                Feature
                                            </button>
                                            <button
                                                className={`col-btn btn-tgt ${isTarget ? 'active-target' : ''}`}
                                                onClick={() => setTarget(col.name)}
                                            >
                                                Target
                                            </button>
                                        </div>
                                    </div>
                                );
                            })}
                        </div>

                        <div className="action-row">
                            <button
                                className="btn btn-primary"
                                onClick={handleContinue}
                                style={{
                                    opacity: isReady ? 1 : 0.4,
                                    pointerEvents: isReady ? 'auto' : 'none'
                                }}
                            >
                                Continue to Architecture Builder ‚Üí
                            </button>
                        </div>
                    </div>
                </>
            )}
        </>
    );
};

